// 这个节点的输入：来自「获取已存视频ID」
// 它的 json 结构大概是：{ has_more: false, items: [ { fields: { ID: [ { text: "xxx" } ], ... }, record_id: "..." }, ... ] }

const root = $input.first().json;      // 只有 1 个 item，里面有 items 数组
const records = root.items || [];

const allIds = records
  .map(r => r.fields?.ID?.[0]?.text)   // 取每条记录的 ID 文本
  .filter(id => !!id);                 // 过滤掉空值

// ✅ 跨节点获取 Webhook 节点输出的 keyword 字段和值（稳定取 body.keyword）
const webhookKeyword =
  $node["Webhook"]?.json?.body?.keyword ??
  $node["Webhook"]?.json?.keyword ??
  "";

// ✅ 方案 1：输出为 1 条 item，同时包含 all_ids + keyword
return [
  {
    json: {
      all_ids: allIds,
      keyword: webhookKeyword
    }
  }
];

const items = $input.all();
const out = [];

// 从获取飞书tenant_access_token节点拿 token
const tokenNode = $node["获取飞书tenant_access_token"];
const tenantToken = tokenNode.json?.tenant_access_token || "";

for (const item of items) {
  const binary = item.binary || {};
  const bin = binary.data;

  if (!bin || !bin.data) continue;

  // 1. 先拿原始 fileName
  let fileName = bin.fileName || bin.filename || "";

  // 2. 如果没有名字，就用时间戳生成
  if (!fileName) fileName = `${Date.now()}.jpg`;

  // 3. 如果没有图片后缀，强制补一个 .jpg
  if (!/\.(jpg|jpeg|png|gif|webp)$/i.test(fileName)) {
    fileName = fileName + ".jpg";
  }

  // 4. MIME 类型兜底为 image/jpeg
  const mimeType = bin.mimeType || "image/jpeg";

  // 5. 计算文件大小（字节数）
  const size = Buffer.from(bin.data, "base64").length;

  // ✅ 关键：透传上一个节点传入的所有字段（item.json 原样保留）
  out.push({
    json: {
      ...(item.json || {}), // 透传全部字段
      file_name: fileName,
      size,
      mime_type: mimeType,
      tenant_access_token: tenantToken,
    },
    binary: {
      data: bin,
    },
  });
}

return out;

// 当前节点输入：来自 Merge-合并上传结果（同一条item里已包含原始字段 + data.file_token）
const cur = $json;

// 上传返回里的 file_token
const fileToken = cur?.data?.file_token || cur?.file_token || "";

// 直接在原字段上追加封面 token（不要丢掉其它字段）
return [{
  json: {
    ...cur,
    封面_file_token: fileToken,
  },
}];

// ================== 工具函数：时间戳转北京时间 yyyy-MM-dd ==================
function formatToBeijing(timestamp) {
  if (!timestamp) return "";

  let ms = Number(timestamp);
  if (Number.isNaN(ms)) return "";

  // 兼容秒级时间戳：小于 10^12 认为是秒
  if (ms < 1e12) ms = ms * 1000;

  const beijingMs = ms + 8 * 60 * 60 * 1000;
  const d = new Date(beijingMs);

  const pad = (n) => (n < 10 ? "0" + n : "" + n);
  const y = d.getUTCFullYear();
  const m = pad(d.getUTCMonth() + 1);
  const day = pad(d.getUTCDate());

  return `${y}-${m}-${day}`;
}

// 根据 kw 生成一组可接受的匹配形式：原始短语 / 去空格 / 拆分成单词
function buildKwCandidates(kw) {
  const base = (kw || "").toLowerCase().trim();
  if (!base) return [];

  const candidates = new Set();
  candidates.add(base);

  const noSpace = base.replace(/\s+/g, "");
  if (noSpace && noSpace !== base) candidates.add(noSpace);

  const parts = base.split(/\s+/).filter(Boolean);
  for (const p of parts) candidates.add(p);

  return Array.from(candidates);
}

// ✅ 清洗 search_input：去掉外层引号、反斜杠转义
function cleanSearchInput(v) {
  if (v === null || v === undefined) return "";
  let s = String(v).trim();

  // 1) 去掉形如 "\"xxx\"" 的外层引号（只剥一层）
  if (
    (s.startsWith('"') && s.endsWith('"')) ||
    (s.startsWith("'") && s.endsWith("'"))
  ) {
    s = s.slice(1, -1).trim();
  }

  // 2) 处理反斜杠转义：把 \" 变成 "，\\ 变成 \
  // 用 JSON.parse 方式最稳：先包一层引号再 parse
  try {
    // 注意：如果 s 本身含有未转义的引号，需要先转义
    const escaped = s.replace(/\\/g, "\\\\").replace(/"/g, '\\"');
    s = JSON.parse(`"${escaped}"`);
  } catch (e) {
    // fallback：尽量手工替换常见转义
    s = s.replace(/\\"/g, '"').replace(/\\\\/g, "\\");
  }

  // 3) 去掉仍残留的首尾引号（保险）
  s = s.replace(/^["']+|["']+$/g, "").trim();

  return s;
}

// ================== 1. 拿上下文（当前产品 + 生成搜索关键词 + 格式化输出） ==================
const apifyItems = $input.all();
const out = [];

// ---- 1.1 当前循环的产品上下文（Loop Over Items） ----
let cur = {};
try {
  cur = $node["Loop Over Items"].json || {};
} catch (e) {
  cur = {};
}

const currentProductName = cur.product_name || cur["产品名称"] || "";
const currentSearchKeyword = (cur.search_keyword || "").toString().toLowerCase().trim();

// ---- 1.2 从「生成搜索关键词」节点拿 search_keyword（仅用于过滤，不再输出） ----
let genSearchKeyword = "";
try {
  const genJson = $node["生成搜索关键词"].json || {};
  genSearchKeyword = (genJson.search_keyword || genJson["search_keyword"] || "")
    .toString()
    .toLowerCase()
    .trim();
} catch (e) {
  genSearchKeyword = "";
}

// ---- 1.3 从「内容格式化输出」节点拿兜底的产品名称（仅用于过滤兜底） ----
let productNameFromFmt = currentProductName;
try {
  const fmtJson = $node["内容格式化输出"].json || {};
  productNameFromFmt = fmtJson.product_name || fmtJson["产品名称"] || productNameFromFmt;
} catch (e) {}

// ---- 1.4 最终用于过滤的关键词：优先 用 生成搜索关键词 → 当前 search_keyword → 产品名 ----
const kwBase = (genSearchKeyword || currentSearchKeyword || (productNameFromFmt || currentProductName || ""))
  .toString()
  .toLowerCase()
  .trim();

const kwCandidates = buildKwCandidates(kwBase);

// ================== 2. 处理 LinkedIn 返回的每条帖子 ==================
for (const item of apifyItems) {
  // ✅ 兼容 item.json 顶层是数组的情况
  const arr = Array.isArray(item.json) ? item.json : [item.json];

  for (const p of arr) {
    if (!p) continue;

    if (!p.activity_id && !p.post_url) continue;

    const article = p.article || {};
    const stats = p.stats || {};
    const postedAt = p.posted_at || p.postedAt || {};

    const rawText = p.text || article.title || "";
    const fullText = String(rawText).replace(/\s+/g, " ").trim();
    if (!fullText) continue;

    // ================== 二次文本过滤 ==================
    if (kwCandidates.length > 0) {
      const textLower = fullText.toLowerCase();
      let hit = false;

      for (const k of kwCandidates) {
        if (!k) continue;
        if (textLower.includes(k)) {
          hit = true;
          break;
        }
      }
      if (!hit) continue;
    }
    // ================================================

    // 点赞数：优先 LIKE，其次 total_reactions
    let likeCount = stats.total_reactions || 0;
    if (Array.isArray(stats.reactions)) {
      const likeObj = stats.reactions.find((r) => String(r.type || "").toUpperCase() === "LIKE");
      if (likeObj && typeof likeObj.count === "number") likeCount = likeObj.count;
    }

    const rawTimestamp = postedAt.timestamp || p.timestamp || Date.now();
    const beijingTimeStr = formatToBeijing(rawTimestamp);

    // 作者信息
    const author = p.author || {};
    const authorName = author.name || "";
    const authorProfileUrl = author.profile_url || "";

    // 分享数
    const shareCount = typeof stats.shares === "number" ? stats.shares : (stats.shares || 0);

    // 标签
    const hashtags = Array.isArray(p.hashtags) ? p.hashtags : [];

    // ✅ 产品名称：取 search_input，并清洗掉引号/反斜杠
    const productNameForOutput = cleanSearchInput(p.search_input || "");

    // ================== 按你的规则处理 content（支持 video / image / article） ==================
    const content = p.content || article.content || {};
    const cType = String(content.type || "").toLowerCase().trim();

    let videoUrl = "";
    let coverUrl = "";
    let content_type = "";

    if (cType === "video") {
      videoUrl = content.url || "";
      coverUrl = content.thumbnail_url || "";
      content_type = "video";
    } else if (cType === "image") {
      coverUrl = content.url || "";
      content_type = "image";
    } else if (cType === "article") {
      const a = content.article || {};
      videoUrl = a.url || content.url || "";
      coverUrl = a.thumbnail_url || content.thumbnail_url || "";
      content_type = "article";
    }

    // 兜底：如果没有封面，尽量给一个
    if (!coverUrl) {
      coverUrl = article.thumbnail_url || author.image_url || p.image_url || "";
    }
    // ============================================================

    out.push({
      产品名称: productNameForOutput,
      ID: p.activity_id || "",
      平台: "LinkedIn",
      原文链接: p.post_url || "",
      内容: fullText,
      点赞: likeCount,
      评论: stats.comments || 0,
      分享: shareCount,
      标签: hashtags,
      作者昵称: authorName,
      作者主页: authorProfileUrl,
      发布时间: beijingTimeStr,
      video_id: p.activity_id || "",
      视频链接: videoUrl,
      素材封面: coverUrl,
      content_type,
    });
  }
}

// ✅ n8n Code 节点必须返回 [{ json: {...} }, ...]
return out.map((x) => ({ json: x }));
